import React, { useState } from "react";
import axios from "axios";
import Radio from "./radio";

const Home = ({ useQuery }) => {
  const [type, setType] = useState(true);
  const [val, setVal] = useState("");
  const [next, setNext] = useState(1);
  const fetchData = async () => {
    let url = "https://openlibrary.org/search.json";
    let parameters = {
      limit: 10,
      page: next,
    };
    if (type) {
      delete parameters.author;
      parameters.title = val;
    } else {
      delete parameters.title;
      parameters.author = val;
    }
    const response = await axios.get(url, {
      params: parameters,
    });
    return response;
  };

  const { isLoading, data, isError, error, refetch, isFetching } = useQuery(
    ["Books", next],
    fetchData,
    {
      enabled: false,
      refetchOnWindowFocus: false,
      staleTime: 30000,
    }
  );

  if (isError) {
    console.log(error);
  }
  console.log(data);
  const handleSearch = (boolean) => {
    if (boolean) {
      setNext(next + 1);
    } else if (next > 1) {
      setNext(next - 1);
    }
    refetch();
  };

  const popular = [
    "JavaScript",
    "Harry Potter",
    "Indian History",
    "Crypto Currency",
    "Criminal law",
  ];

  return (
    <div>
      {isLoading || isFetching ? <h1> Async doing </h1> : ""}
      <input type="text" value={val} onChange={(e) => setVal(e.target.value)} />
      <button
        onClick={() => {
          setNext(1);
          refetch();
        }}
      >
        {" "}
        Search{" "}
      </button>
      <Radio type={type} setType={setType} />
      <div>
        {popular.map((value) => {
          return (
            <div>
              <a
                target="_blank"
                href={`https://openlibrary.org/search?q=${value}`}
              >
                {value}
              </a>
              <br />
            </div>
          );
        })}
      </div>

      {data ? (
        <div>
          <table>
            <tbody>
              <tr>
                <td> Title and Sub Title </td>
                <td> Author </td>
                <td> Latest Publish year</td>
                <td> First Publish year</td>
              </tr>

              {data.data.docs.map((inner) => {
                return (
                  <tr>
                    <td>
                      {inner.title} {inner.subtitle}
                    </td>
                    <td> {inner.author_name[0]}</td>
                    <td> {Math.max.apply(null, inner.publish_year)}</td>
                    <td> {Math.min.apply(null, inner.publish_year)}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          <button
            onClick={() => {
              handleSearch(false);
            }}
          >
            Previous
          </button>
          <button
            onClick={() => {
              handleSearch(true);
            }}
          >
            Next
          </button>
        </div>
      ) : (
        ""
      )}
    </div>
  );
};

export default Home;
