import React from "react";
import styles from "../static/css/home.module.css";
const Radio = ({ type, setType }) => {
  return (
    <div className={styles.radioFlex}>
      <label htmlFor="radio1" className={styles.radioButton1}></label>
      <input
        id="radio1"
        type="radio"
        name="radio"
        checked={type}
        onChange={() => setType(!type)}
      />
      Title
      <label htmlFor="radio2" className={styles.radioButton2}>
        Author
      </label>
      <input
        id="radio2"
        type="radio"
        name="radio"
        checked={!type}
        onChange={() => setType(!type)}
      />
    </div>
  );
};

export default Radio;
