import React, { useState } from "react";
import axios from "axios";
import { usePromiseTracker, trackPromise } from "react-promise-tracker";

const LoadingIndicator = () => {
  const { promiseInProgress } = usePromiseTracker();
  return promiseInProgress && <h1>Hey some async call in progress ! </h1>;
};

const Radio = ({ type, setType }) => {
  return (
    <div>
      <div>
        <label>
          <input
            type="radio"
            name="radio"
            checked={type}
            onChange={() => setType(!type)}
          />
          Title
        </label>
      </div>
      <div>
        <label>
          <input
            type="radio"
            name="radio"
            checked={!type}
            onChange={() => setType(!type)}
          />
          Author
        </label>
      </div>
    </div>
  );
};

const Home = () => {
  const [type, setType] = useState(true);
  const [books, setBooks] = useState([{}]);
  const [val, setVal] = useState("");
  const handleSearch = async () => {
    let url = "https://openlibrary.org/search.json";
    if (type) {
      delete parameters.author;
      parameters.title = val;
    } else {
      delete parameters.title;
      parameters.author = val;
    }
    const response = await trackPromise(
      axios.get(url, {
        params: parameters,
      })
    );
    setBooks([response.data.docs]);
    console.log(response);
  };

  const popular = [
    "JavaScript",
    "Harry Potter",
    "Indian History",
    "Crypto Currency",
    "Criminal law",
  ];

  return (
    <div>
      <LoadingIndicator />
      <input type="text" value={val} onChange={(e) => setVal(e.target.value)} />
      <button onClick={handleSearch}> Search </button>
      <Radio type={type} setType={setType} />
      <div>
        {popular.map((value) => {
          return (
            <div>
              <a
                target="_blank"
                href={`https://openlibrary.org/search?q=${value}`}
              >
                {value}
              </a>
              <br />
            </div>
          );
        })}
      </div>
      {Object.keys(books[0]).length ? (
        <table>
          <tbody>
            <tr>
              <td> Title and Sub Title </td>
              <td> Author </td>
              <td> Latest Publish year</td>
              <td> First Publish year</td>
            </tr>

            {books.map((val) => {
              return val.map((inner) => {
                return (
                  <tr>
                    <td>
                      {inner.title} {inner.subtitle}
                    </td>
                    <td> {inner.author_name[0]}</td>
                    <td> {Math.max.apply(null, inner.publish_year)}</td>
                    <td> {Math.min.apply(null, inner.publish_year)}</td>
                  </tr>
                );
              });
            })}
          </tbody>
        </table>
      ) : (
        <h1> Degro </h1>
      )}
    </div>
  );
};

export default Home;
