import React, { useState } from "react";
import axios from "axios";
import Radio from "./radio";
import { Link } from "react-router-dom";
import styles from "../static/css/home.module.css";

const Home = ({ useQuery }) => {
  const [type, setType] = useState(true);
  const [val, setVal] = useState("");
  const [next, setNext] = useState(1);
  const [search, setSearch] = useState(false);
  const fetchData = async () => {
    let url = "https://openlibrary.org/search.json";
    let parameters = {
      limit: 10,
      page: next,
    };
    if (type) {
      delete parameters.author;
      parameters.title = val;
    } else {
      delete parameters.title;
      parameters.author = val;
    }
    const response = await axios.get(url, {
      params: parameters,
    });
    setSearch(true);
    return response;
  };

  const { isLoading, data, isError, error, refetch, isFetching } = useQuery(
    ["Books", next],
    fetchData,
    {
      enabled: search,
      refetchOnWindowFocus: false,
    }
  );

  if (isError) {
    console.log(error);
  }
  const handleSearch = (boolean) => {
    if (boolean) {
      setNext(next + 1);
    } else if (next > 1) {
      setNext(next - 1);
    }
  };

  const popular = [
    "JavaScript",
    "Harry Potter",
    "Indian History",
    "Crypto Currency",
    "Criminal law",
  ];

  return (
    <div>
      {isLoading || isFetching ? (
        <h1> Async doing </h1>
      ) : (
        <h1 className={styles.header}> Search for Any Book</h1>
      )}
      <input
        className={styles.searchField}
        type="text"
        value={val}
        onChange={(e) => setVal(e.target.value)}
      />
      <button
        className={styles.searchButton}
        onClick={() => {
          setNext(1);
          refetch();
        }}
      >
        {" "}
        Search{" "}
      </button>
      <Radio type={type} setType={setType} />
      <div className={styles.subjectList}>
        {popular.map((value) => {
          return (
            <div className={styles.individualSubjects}>
              <Link to={`/Subjects/${value}`}> {value} </Link>
              <br />
            </div>
          );
        })}
      </div>

      {data ? (
        <div className={styles.tableContainer}>
          <table className={styles.table}>
            <tbody>
              <tr>
                <td> Title and Sub Title </td>
                <td> Author </td>
                <td> Latest Publish year</td>
                <td> First Publish year</td>
              </tr>

              {data.data.docs.map((inner) => {
                return (
                  <tr>
                    <td>
                      {inner.title} {inner.subtitle}
                    </td>
                    <td> {inner.author_name[0]}</td>
                    <td> {Math.max.apply(null, inner.publish_year)}</td>
                    <td> {Math.min.apply(null, inner.publish_year)}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          <button
            className={styles.traverse}
            onClick={() => {
              handleSearch(false);
              refetch();
            }}
          >
            Previous
          </button>
          <button
            className={styles.traverse}
            onClick={() => {
              handleSearch(true);
              refetch();
            }}
          >
            Next
          </button>
        </div>
      ) : (
        ""
      )}
    </div>
  );
};

export default Home;
