import React, { useState } from "react";
import axios from "axios";
import { usePromiseTracker, trackPromise } from "react-promise-tracker";

const LoadingIndicator = () => {
  const { promiseInProgress } = usePromiseTracker();
  return promiseInProgress && <h1>Hey some async call in progress ! </h1>;
};

const Radio = ({ type, setType }) => {
  return (
    <div>
      <div>
        <label>
          <input
            type="radio"
            name="radio"
            checked={type}
            onChange={() => setType(!type)}
          />
          Title
        </label>
      </div>
      <div>
        <label>
          <input
            type="radio"
            name="radio"
            checked={!type}
            onChange={() => setType(!type)}
          />
          Author
        </label>
      </div>
    </div>
  );
};

const Home = () => {
  const [books, setBooks] = useState([{}]);
  const [val, setVal] = useState("");
  const handleSearch = async () => {
    let url = "https://openlibrary.org/search.json";
    const response = await trackPromise(
      axios.get(url, {
        params: {
          q: val,
          limit: 10,
        },
      })
    );
    setBooks([response.data.docs]);
  };

  const popular = [
    "JavaScript",
    "Harry Potter",
    "Indian History",
    "Crypto Currency",
    "Criminal law",
  ];
  const [type, setType] = useState(true);
  return (
    <div>
      <LoadingIndicator />
      <input type="text" value={val} onChange={(e) => setVal(e.target.value)} />
      <button onClick={handleSearch}> Search </button>
      <Radio type={type} setType={setType} />
      <div>
        {popular.map((value) => {
          return (
            <div>
              <a
                target="_blank"
                href={`https://openlibrary.org/search?q=${value}`}
              >
                {value}
              </a>
              <br />
            </div>
          );
        })}
      </div>
      {Object.keys(books[0]).length ? (
        <div>
          {books.map((val) => {
            return val.map((inner) => {
              return inner.title;
            });
          })}{" "}
        </div>
      ) : (
        <h1> Degro </h1>
      )}
    </div>
  );
};

export default Home;
